  if (x < 0) {
    stop("Error: x cannot be negative")
  } else {
    return(sqrt(x))
  }
}

# Call the function with valid input
result1 <- tryCatch({
  my_function(25)
}, error = function(e) {
  print(paste("Caught an exception:", e$message))
})

# Print the result
print(result1)

# Call the function with invalid input
result2 <- tryCatch({
  my_function(-10)
}, error = function(e) {
  print(paste("Caught an exception:", e$message))
})

