int nFibonacci(int n){
   vector<int> fib(n + 1);
    fib[0] = 0;
    fib[1] = 1;
    for(int i = 2;i <= n;i ++){
         fib[i] = fib[i - 1] + fib[i - 2];
    }
    return fib[n]; 
} /*Sample C++ code for finding nth fibonacci number with DP*/
